alter table categories enable row level security; create policy "Enable read access for all users" on categories for select using (true); create policy "Enable insert for authenticated users" on categories for insert with check (auth.role() = 'authenticated'); create policy "Enable update for authenticated users" on categories for update using (auth.role() = 'authenticated'); create policy "Enable delete for authenticated users" on categories for delete using (auth.role() = 'authenticated');
